// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./TronicToken.sol";
import "./TronicMembership.sol";
import "./interfaces/IERC6551Account.sol";
import "lib/tokenbound/lib/openzeppelin-contracts/contracts/proxy/Clones.sol";

contract TronicMain {
    struct MembershipInfo {
        address membershipAddress;
        address tokenAddress;
        string membershipName;
    }

    enum TokenType {
        ERC1155,
        ERC721
    }

    event MembershipAdded(
        uint256 indexed membershipId,
        address indexed membershipAddress,
        address indexed tokenAddress
    );

    event MembershipRemoved(uint256 indexed membershipId);

    address public owner;
    address public tronicAdmin;
    address payable public tbaAccountImplementation;

    uint8 public maxTiersPerMembership = 10;

    uint256 public membershipCounter;
    mapping(uint256 => MembershipInfo) private memberships;
    mapping(address => bool) private _admins;

    // Deployments
    IERC6551Registry public registry;
    TronicMembership public tronicMembership;
    TronicToken public tronicERC1155;

    /// @notice Constructs the TronicMain contract.
    /// @param _admin The address of the Tronic admin.
    /// @param _tronicMembership The address of the Tronic Membership contract (ERC721 implementation).
    /// @param _tronicToken The address of the Tronic Token contract (ERC1155 implementation).
    /// @param _registry The address of the registry contract.
    /// @param _tbaImplementation The address of the tokenbound account implementation.
    constructor(
        address _admin,
        address _tronicMembership,
        address _tronicToken,
        address _registry,
        address _tbaImplementation
    ) {
        owner = msg.sender;
        tronicAdmin = _admin;
        tronicERC1155 = TronicToken(_tronicToken);
        tronicMembership = TronicMembership(_tronicMembership);
        registry = IERC6551Registry(_registry);
        tbaAccountImplementation = payable(_tbaImplementation);
    }

    /// @notice Checks if the caller is the owner.
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    /// @notice Checks if the caller is an admin.
    modifier onlyAdmin() {
        require(_admins[msg.sender] || msg.sender == tronicAdmin, "Only admin");
        _;
    }

    /// @notice Gets MembershipInfo for a given membership ID.
    /// @param membershipId The ID of the membership to get info for.
    /// @return The MembershipInfo struct for the given membership ID.
    /// @dev The membership ID is the index of the membership in the memberships mapping.
    function getMembershipInfo(uint256 membershipId)
        external
        view
        returns (MembershipInfo memory)
    {
        return memberships[membershipId];
    }

    /// @notice Deploys a new membership's contracts.
    /// @param membershipName The membership name for the ERC721 token.
    /// @param membershipSymbol The membership symbol for the ERC721 token.
    /// @param membershipBaseURI The base URI for the membership ERC721 token.
    /// @param maxMintable The maximum number of memberships that can be minted.
    /// @return memberId The ID of the newly created membership.
    /// @return membershipAddress The address of the deployed membership ERC721 contract.
    /// @return tokenAddress The address of the deployed token ERC1155 contract.
    /// @dev The membership ID is the index of the membership in the memberships mapping.
    function deployMembership(
        string memory membershipName,
        string memory membershipSymbol,
        string memory membershipBaseURI,
        uint256 maxMintable,
        bool isElastic,
        bool isBound
    )
        external
        onlyAdmin
        returns (uint256 memberId, address membershipAddress, address tokenAddress)
    {
        memberId = membershipCounter++;

        // Deploy the membership's contracts
        membershipAddress = _deployMembership(
            membershipName, membershipSymbol, membershipBaseURI, maxMintable, isElastic, isBound
        );
        tokenAddress = _deployToken();

        // Assign membership id and associate the deployed contracts with the membership
        memberships[memberId] = MembershipInfo({
            membershipAddress: membershipAddress,
            tokenAddress: tokenAddress,
            membershipName: membershipName
        });

        emit MembershipAdded(memberId, membershipAddress, tokenAddress);
    }

    /// @notice Clones the Tronic Membership (ERC721) implementation and initializes it.
    /// @param name The name of the token.
    /// @param symbol The symbol of the token.
    /// @param uri The URI for the cloned contract.
    /// @return membershipAddress The address of the newly cloned Membership contract.
    function _deployMembership(
        string memory name,
        string memory symbol,
        string memory uri,
        uint256 maxSupply,
        bool isElastic,
        bool isBound
    ) private returns (address membershipAddress) {
        membershipAddress = Clones.clone(address(tronicMembership));
        TronicMembership(membershipAddress).initialize(
            tbaAccountImplementation,
            address(registry),
            name,
            symbol,
            uri,
            maxTiersPerMembership,
            maxSupply,
            isElastic,
            isBound,
            tronicAdmin
        );
    }

    /// @notice Clones the ERC1155 implementation and initializes it.
    /// @return tokenAddress The address of the newly cloned ERC1155 contract.
    function _deployToken() private returns (address tokenAddress) {
        tokenAddress = Clones.clone(address(tronicERC1155));
        TronicToken(tokenAddress).initialize(tronicAdmin);
    }

    /// @notice Removes a membership from the contract.
    /// @param _membershipId The ID of the membership to remove.
    function removeMembership(uint256 _membershipId) external onlyAdmin {
        delete memberships[_membershipId];
    }

    /// @notice Creates a new ERC1155 fungible token type for a membership.
    /// @param maxSupply The maximum supply of the token type.
    /// @param uri The URI for the token type.
    /// @param membershipId The ID of the membership to create the token type for.
    /// @return typeId The ID of the newly created token type.
    function createFungibleTokenType(uint256 maxSupply, string memory uri, uint256 membershipId)
        external
        onlyAdmin
        returns (uint256 typeId)
    {
        MembershipInfo memory membership = memberships[membershipId];
        require(membership.tokenAddress != address(0), "Membership does not exist");
        typeId = TronicToken(membership.tokenAddress).createFungibleType(uint64(maxSupply), uri);
    }

    /// @notice Creates a new ERC1155 non-fungible token type for a membership.
    /// @param baseUri The URI for the token type.
    /// @param maxMintable The maximum number of tokens that can be minted.
    /// @param membershipId The ID of the membership to create the token type for.
    /// @return nftTypeID The ID of the newly created token type.
    function createNonFungibleTokenType(
        string memory baseUri,
        uint64 maxMintable,
        uint256 membershipId
    ) external onlyAdmin returns (uint256 nftTypeID) {
        MembershipInfo memory membership = memberships[membershipId];
        require(membership.tokenAddress != address(0), "Membership does not exist");
        nftTypeID = TronicToken(membership.tokenAddress).createNFTType(baseUri, maxMintable);
    }

    /// @notice Mints a new ERC721 token for a specified membership.
    /// @param _recipient The address to mint the token to.
    /// @param _membershipId The ID of the membership to mint the token for.
    /// @return The address of the newly created token account.
    function mintMembership(address _recipient, uint256 _membershipId)
        external
        onlyAdmin
        returns (address payable)
    {
        MembershipInfo memory membership = memberships[_membershipId];
        require(membership.membershipAddress != address(0), "Membership does not exist");
        return TronicMembership(membership.membershipAddress).mint(_recipient);
    }

    /// @notice Mints a fungible ERC1155 token.
    /// @param _membershipId The ID of the membership to mint the token for.
    /// @param _recipient The address to mint the token to.
    /// @param _tokenId The tokenID (same as typeID for fungibles) of the token to mint.
    /// @param _amount The amount of the token to mint.
    function mintFungibleToken(
        uint256 _membershipId,
        address _recipient,
        uint256 _tokenId,
        uint64 _amount
    ) external onlyAdmin {
        MembershipInfo memory membership = memberships[_membershipId];
        require(membership.tokenAddress != address(0), "Membership does not exist");
        TronicToken(membership.tokenAddress).mintFungible(_recipient, _tokenId, _amount);
    }

    /// @notice Mints a new nonfungible ERC1155 token.
    /// @param _membershipId The ID of the membership to mint the token for.
    /// @param _recipient The address to mint the token to.
    /// @param _typeId The typeID of the token to mint.
    /// @param _amount The amount of the token to mint.
    function mintNonFungibleToken(
        uint256 _membershipId,
        address _recipient,
        uint256 _typeId,
        uint256 _amount
    ) external onlyAdmin {
        MembershipInfo memory membership = memberships[_membershipId];
        require(membership.tokenAddress != address(0), "Membership does not exist");
        TronicToken(membership.tokenAddress).mintNFTs(_typeId, _recipient, _amount);
    }

    /// @notice Processes multiple minting operations for both ERC1155 and ERC721 tokens on behalf of memberships.
    /// @param _membershipIds   Array of membership IDs corresponding to each minting operation.
    /// @param _recipients   2D array of recipient addresses for each minting operation.
    /// @param _tokenTypeIDs     4D array of token TypeIDs to mint for each membership.
    ///                      For ERC1155, it could be multiple IDs, and for ERC721, it should contain a single ID.
    /// @param _amounts      4D array of token amounts to mint for each membership.
    ///                      For ERC1155, it represents the quantities of each token ID, and for ERC721, it should be either [1] (to mint) or [0] (to skip).
    /// @param _contractTypes   3D array specifying the type of each token contract (either ERC1155 or ERC721) to determine the minting logic.
    /// @dev Requires that all input arrays have matching lengths.
    ///      For ERC721 minting, the inner arrays of _tokenTypes and _amounts should have a length of 1.
    /// @dev array indexes: _tokenTypeIDs[membershipId][recipient][contractType][tokenTypeIDs]
    /// @dev array indexes: _amounts[membershipId][recipient][contractType][amounts]
    function batchProcess(
        uint256[] memory _membershipIds,
        address[][] memory _recipients,
        uint256[][][][] memory _tokenTypeIDs,
        uint256[][][][] memory _amounts,
        TokenType[][][] memory _contractTypes
    ) external onlyAdmin {
        require(
            _membershipIds.length == _tokenTypeIDs.length && _tokenTypeIDs.length == _amounts.length
                && _amounts.length == _recipients.length && _recipients.length == _contractTypes.length,
            "Outer arrays must have the same length"
        );

        // i = membershipId, j = recipient, k = contracttype
        // Loop through each membership
        for (uint256 i = 0; i < _membershipIds.length; i++) {
            MembershipInfo memory membership = memberships[_membershipIds[i]];

            for (uint256 j = 0; j < _recipients[i].length; j++) {
                address recipient = _recipients[i][j];

                for (uint256 k = 0; k < _contractTypes[i][j].length; k++) {
                    if (_contractTypes[i][j][k] == TokenType.ERC1155) {
                        TronicToken(membership.tokenAddress).mintBatch(
                            recipient, _tokenTypeIDs[i][j][k], _amounts[i][j][k], ""
                        );
                    } else if (_contractTypes[i][j][k] == TokenType.ERC721) {
                        TronicMembership(membership.membershipAddress).mint(recipient);
                    }
                }
            }
        }
    }

    /// @notice transfers tokens from a membership TBA to a specified address
    /// @param _tronicTokenId The ID of the tronic token that owns the Tronic TBA
    /// @param _membershipId The ID of the membership that issued the membership TBA
    /// @param _membershipTokenId The ID of the membership TBA
    /// @param _to The address to transfer the tokens to
    /// @param _transferTokenId The ID of the token to transfer
    /// @param _amount The amount of tokens to transfer
    /// @dev This contract address must be granted permissions to transfer tokens from the membership TBA
    /// @dev The membership TBA must be owned by the Tronic tokenId TBA
    /// @dev This function is only callable by the tronic admin or an authorized account
    function transferTokensFromMembershipTBA(
        uint256 _tronicTokenId,
        uint256 _membershipId,
        uint256 _membershipTokenId,
        address _to,
        uint256 _transferTokenId,
        uint256 _amount
    ) external {
        // get Tronic TBA address for tronic token id
        address payable tronicTbaAddress = payable(tronicMembership.getTBAccount(_tronicTokenId));
        IERC6551Account tronicTBA = IERC6551Account(tronicTbaAddress);

        //ensure caller is tronic admin or authorized to transfer tokens
        require(
            tronicTBA.isAuthorized(msg.sender) || _admins[msg.sender] || msg.sender == tronicAdmin,
            "Unauthorized caller"
        );

        // get membership info
        MembershipInfo memory membership = memberships[_membershipId];
        require(membership.tokenAddress != address(0), "Membership does not exist");

        // get Membership TBA address
        address membershipTbaAddress =
            TronicMembership(membership.membershipAddress).getTBAccount(_membershipTokenId);

        // construct SafeTransferCall for membership ERC1155
        bytes memory tokenTransferCall = abi.encodeWithSignature(
            "safeTransferFrom(address,address,uint256,uint256,bytes)",
            membershipTbaAddress,
            _to,
            _transferTokenId,
            _amount,
            ""
        );

        // construct execute call for membership tbaAddress to execute tokenTransferCall
        bytes memory executeCall = abi.encodeWithSignature(
            "executeCall(address,uint256,bytes)", membership.tokenAddress, 0, tokenTransferCall
        );

        tronicTBA.executeCall(membershipTbaAddress, 0, executeCall);
    }

    /// @notice transfers tokens from a tronic TBA to a specified address
    /// @param _tronicTokenId The ID of the tronic token that owns the Tronic TBA
    /// @param _transferTokenId The ID of the token to transfer
    /// @param _amount The amount of tokens to transfer
    /// @param _to The address to transfer the tokens to
    /// @dev This contract address must be granted permissions to transfer tokens from the Tronic token TBA
    /// @dev The tronic TBA must be owned by the Tronic tokenId TBA
    /// @dev This function is only callable by the tronic admin or an authorized account
    function transferTokensFromTronicTBA(
        uint256 _tronicTokenId,
        uint256 _transferTokenId,
        uint256 _amount,
        address _to
    ) external {
        // get Tronic TBA address for tronic token id
        address payable tronicTbaAddress = payable(tronicMembership.getTBAccount(_tronicTokenId));
        IERC6551Account tronicTBA = IERC6551Account(tronicTbaAddress);

        //ensure caller is tronic admin or authorized to transfer tokens
        require(
            tronicTBA.isAuthorized(msg.sender) || _admins[msg.sender] || msg.sender == tronicAdmin,
            "Unauthorized caller"
        );

        // construct SafeTransferCall for tronic ERC1155
        bytes memory tokenTransferCall = abi.encodeWithSignature(
            "safeTransferFrom(address,address,uint256,uint256,bytes)",
            tronicTbaAddress,
            _to,
            _transferTokenId,
            _amount,
            ""
        );

        tronicTBA.executeCall(address(tronicERC1155), 0, tokenTransferCall);
    }

    /// @notice transfers membership from a tronic TBA to a specified address
    /// @param _tronicTokenId The ID of the tronic token that owns the Tronic TBA
    /// @param _membershipId The ID of the membership that issued the membership TBA
    /// @param _membershipTokenId The ID of the membership TBA
    /// @param _to The address to transfer the membership to
    /// @dev This contract address must be granted permissions to transfer tokens from the Tronic token TBA
    /// @dev The membership token TBA must be owned by the Tronic token TBA
    function transferMembershipFromTronicTBA(
        uint256 _tronicTokenId,
        uint256 _membershipId,
        uint256 _membershipTokenId,
        address _to
    ) external {
        // get Tronic TBA address for tronic token id
        address payable tronicTbaAddress = payable(tronicMembership.getTBAccount(_tronicTokenId));
        IERC6551Account tronicTBA = IERC6551Account(tronicTbaAddress);
        //ensure caller is either admin or authorized to transfer tokens
        require(
            tronicTBA.isAuthorized(msg.sender) || _admins[msg.sender] || msg.sender == tronicAdmin,
            "Unauthorized caller"
        );

        // get membership contract address
        address membershipAddress = memberships[_membershipId].membershipAddress;
        require(membershipAddress != address(0), "Membership does not exist");

        // construct and execute SafeTransferCall for membership ERC721
        bytes memory membershipTransferCall = abi.encodeWithSignature(
            "safeTransferFrom(address,address,uint256)", tronicTbaAddress, _to, _membershipTokenId
        );

        tronicTBA.executeCall(membershipAddress, 0, membershipTransferCall);
    }

    /// @notice Sets the ERC721 implementation address, callable only by the owner.
    /// @param newImplementation The address of the new ERC721 implementation.
    function setERC721Implementation(address newImplementation) external onlyOwner {
        tronicMembership = TronicMembership(newImplementation);
    }

    /// @notice Sets the ERC1155 implementation address, callable only by the owner.
    /// @param newImplementation The address of the new ERC1155 implementation.
    function setERC1155Implementation(address newImplementation) external onlyOwner {
        tronicERC1155 = TronicToken(newImplementation);
    }

    /// @notice Sets the account implementation address, callable only by the owner.
    /// @param newImplementation The address of the new account implementation.
    function setAccountImplementation(address payable newImplementation) external onlyOwner {
        tbaAccountImplementation = newImplementation;
    }

    /// @notice Sets the registry address, callable only by the owner.
    /// @param newRegistry The address of the new registry.
    function setRegistry(address newRegistry) external onlyOwner {
        registry = IERC6551Registry(newRegistry);
    }

    /// @notice Adds an admin to the contract.
    /// @param admin The address of the new admin.
    function addAdmin(address admin) external onlyOwner {
        _admins[admin] = true;
    }

    /// @notice Removes an admin from the contract.
    /// @param admin The address of the admin to remove.
    function removeAdmin(address admin) external onlyOwner {
        _admins[admin] = false;
    }

    /// @notice Checks if an address is an admin of the contract.
    /// @param admin The address to check.
    /// @return True if the address is an admin, false otherwise.
    function isAdmin(address admin) external view returns (bool) {
        return _admins[admin] || admin == tronicAdmin;
    }
}

src/TronicToken.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.13;

import "lib/tokenbound/lib/openzeppelin-contracts/contracts/token/ERC1155/ERC1155.sol";
import "lib/tokenbound/lib/openzeppelin-contracts/contracts/proxy/utils/Initializable.sol";
import {Strings} from "lib/tokenbound/lib/openzeppelin-contracts/contracts/utils/Strings.sol";

/// @title TronicToken
/// @notice This contract represents the fungible and non-fungible tokens (NFTs) for the Tronic ecosystem.
/// @dev This contract is based on the ERC1155 standard.
/// @dev This contract is cloneable.
contract TronicToken is ERC1155, Initializable {
    using Strings for uint256;

    struct FungibleTokenInfo {
        uint64 maxSupply;
        uint64 totalMinted;
        uint64 totalBurned;
        string uri;
    }

    struct NFTokenInfo {
        uint64 startingTokenId;
        uint64 totalMinted;
        uint64 maxMintable;
        string baseURI;
    }

    event FungibleTokenTypeCreated(uint256 indexed typeId, uint64 maxSupply, string uri);

    event NFTokenTypeCreated(uint256 indexed typeId, uint64 maxMintable, string baseURI);

    uint32 private _tokenTypeCounter;
    uint64 private _nextNFTTypeStartId = 100_000;
    address public owner;
    string public name;
    string public symbol;
    mapping(uint256 => FungibleTokenInfo) private _fungibleTokens;
    mapping(uint256 => NFTokenInfo) private _nftTypes;
    mapping(uint256 => uint256) public tokenLevels;
    mapping(uint256 => address) public nftOwners;
    mapping(address => bool) private _admins;

    // Token ID => URI mapping for fungible tokens
    mapping(uint256 => string) private _fungibleTokenURIs;

    /// @notice Constructor initializes ERC1155 with an empty URI.
    constructor() ERC1155("") {}

    /// @dev Modifier to ensure only the owner can call certain functions.
    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    /// @dev Modifier to ensure only an admin can call certain functions.
    modifier onlyAdmin() {
        require(_admins[msg.sender], "Only Admin");
        _;
    }

    /// @notice Initializes the contract with tronic Admin address.
    /// @param _tronicAdmin Address of the Tronic admin.
    /// @dev This function is called by the TronicMain contract.
    function initialize(address _tronicAdmin) external initializer {
        owner = _tronicAdmin;
        _admins[_tronicAdmin] = true;
        //sets TronicAdmin.sol as admin
        _admins[msg.sender] = true;
    }

    /// @notice Gets the information of a fungible token type.
    /// @param typeId The ID of the token type.
    /// @return The information of the token type.
    function getFungibleTokenInfo(uint256 typeId)
        external
        view
        returns (FungibleTokenInfo memory)
    {
        return _fungibleTokens[typeId];
    }

    /// @notice Gets the information of a non-fungible token (NFT) type.
    /// @param typeId The ID of the token type.
    /// @return The information of the token type.
    function getNFTokenInfo(uint256 typeId) external view returns (NFTokenInfo memory) {
        return _nftTypes[typeId];
    }

    /// @notice Creates a new non-fungible token (NFT) type.
    /// @param baseURI Base URI for the token metadata.
    /// @param maxMintable Max mintable for the NFT type.
    /// @return nftTypeId The ID of the new NFT type.
    /// @dev Only callable by admin.
    /// @dev Requires that the max mintable is greater than 0.
    function createNFTType(string memory baseURI, uint64 maxMintable)
        external
        onlyAdmin
        returns (uint256 nftTypeId)
    {
        nftTypeId = _tokenTypeCounter++;
        require(_nftTypes[nftTypeId].maxMintable == 0, "Token type already exists");
        require(maxMintable > 0, "Max mintable must be greater than 0");

        _nftTypes[nftTypeId] = NFTokenInfo({
            baseURI: baseURI,
            startingTokenId: _nextNFTTypeStartId += maxMintable,
            totalMinted: 0,
            maxMintable: maxMintable
        });

        emit NFTokenTypeCreated(nftTypeId, maxMintable, baseURI);
    }

    /// @notice Creates a new fungible token type.
    /// @param _maxSupply Max supply for the fungible token type.
    /// @param _uri URI for the token type's metadata.
    /// @return fungibleTokenId The ID of the new fungible token type.
    /// @dev Only callable by admin.
    /// @dev Requires that the max supply is greater than 0.
    function createFungibleType(uint64 _maxSupply, string memory _uri)
        external
        onlyAdmin
        returns (uint256 fungibleTokenId)
    {
        require(_maxSupply > 0, "Max supply must be greater than 0");
        // Increment the fungible token ID counter and set fungibleTokenId.
        fungibleTokenId = _tokenTypeCounter++;
        // Set Fungible Tokens struct for the new token ID.
        _fungibleTokens[fungibleTokenId] =
            FungibleTokenInfo({uri: _uri, maxSupply: _maxSupply, totalMinted: 0, totalBurned: 0});

        emit FungibleTokenTypeCreated(fungibleTokenId, _maxSupply, _uri);
    }

    /// @notice Mints fungible tokens to a specific address.
    /// @param to Address to mint the tokens to.
    /// @param id ID of the fungible token type.
    /// @param amount The amount of tokens to mint.
    /// @dev Requires that the token type exists and minting amount does not exceed max supply.
    function mintFungible(address to, uint256 id, uint64 amount) external onlyAdmin {
        FungibleTokenInfo memory token = _fungibleTokens[id];
        require(bytes(token.uri).length > 0, "Token type does not exist");

        // Increase the totalMinted count
        token.totalMinted += amount;
        require(token.totalMinted <= token.maxSupply, "Exceeds max supply");

        //update the struct
        _fungibleTokens[id] = token;

        _mint(to, id, amount, "");
    }

    /// @notice Mints a non-fungible token (NFT) to a specific address.
    /// @param typeId Type ID of the NFT.
    /// @param to Address to mint the NFT to.
    /// @dev Requires that the NFT type already exists.
    /// @dev Requires that the amount does not exceed the max mintable for the NFT type.
    function mintNFT(uint256 typeId, address to) external onlyAdmin {
        //get memory instance of NFT type
        NFTokenInfo memory nftType = _nftTypes[typeId];

        require(nftType.maxMintable > 0, "NFT type does not exist");
        // Get the next token ID to mint, and increment the totalMinted count
        uint256 tokenId = getNextTokenIdForType(typeId);
        require(
            tokenId <= nftType.startingTokenId + nftType.maxMintable,
            "Exceeds max mintable for this NFT type"
        );

        nftOwners[tokenId] = to; // Update the owner of the NFT

        // update the struct with new totalMinted
        _nftTypes[typeId] = nftType;

        _mint(to, tokenId, 1, "");
    }

    /// @notice Mints multiple non-fungible tokens (NFTs) to a specific address.
    /// @param typeId Type ID of the NFT.
    /// @param to Address to mint the NFTs to.
    /// @param amount The amount of NFTs to mint.
    /// @dev Requires that the NFT type already exists.
    /// @dev Requires that the amount does not exceed the max mintable for the NFT type.
    /// @dev only callable by admin
    function mintNFTs(uint256 typeId, address to, uint256 amount) external onlyAdmin {
        _mintNFTs(typeId, to, amount);
    }

    /// @notice Mints multiple non-fungible tokens (NFTs) to a specific address.
    /// @param typeId Type ID of the NFT.
    /// @param to Address to mint the NFTs to.
    /// @param amount The amount of NFTs to mint.
    /// @dev Requires that the NFT type already exists.
    /// @dev Requires that the amount does not exceed the max mintable for the NFT type.
    function _mintNFTs(uint256 typeId, address to, uint256 amount) internal {
        //get memory instance of NFT type
        NFTokenInfo memory nftType = _nftTypes[typeId];

        require(nftType.maxMintable > 0, "NFT type does not exist");
        require(
            nftType.totalMinted + amount <= nftType.maxMintable,
            "Exceeds max mintable for this NFT type"
        );

        // Build the token ID and amount arrays for batchMint call
        uint256[] memory tokenIds = new uint256[](amount);
        uint256[] memory amounts = new uint256[](amount);
        uint256 _tokenId = getNextTokenIdForType(typeId);

        for (uint256 i = 0; i < amount; i++) {
            tokenIds[i] = _tokenId;
            amounts[i] = 1; // Each NFT token id has an amount of 1

            nftOwners[_tokenId++] = to; // Update the owner of the NFT
        }

        // update the struct with new totalMinted
        _nftTypes[typeId].totalMinted += uint64(amount);

        _mintBatch(to, tokenIds, amounts, "");
    }

    /// @notice Sets the level of a specific token ID.
    /// @param tokenId The ID of the token.
    /// @param level The level to set.
    /// @dev Only callable by admin.
    function setLevel(uint256 tokenId, uint256 level) external onlyAdmin {
        tokenLevels[tokenId] = level;
    }

    /// @notice Gets the level of a specific token ID.
    /// @param tokenId The ID of the token.
    /// @return The level of the token.
    function getLevel(uint256 tokenId) external view returns (uint256) {
        return tokenLevels[tokenId];
    }

    /// @notice Mints multiple tokens to a specific address.
    /// @param to Address to mint the tokens to.
    /// @param typeIds Type IDs of the tokens to mint.
    /// @param amounts Amounts of each token to mint.
    /// @param data Additional data to include in the minting call.
    /// @dev Requires that the token type IDs and amounts arrays have matching lengths.
    function mintBatch(
        address to,
        uint256[] memory typeIds,
        uint256[] memory amounts,
        bytes memory data
    ) external onlyAdmin {
        require(to != address(0), "ERC1155Cloneable: mint to the zero address");
        require(typeIds.length == amounts.length, "Mismatch between typeIds and amounts length");

        uint256[] memory idsToMint = new uint256[](typeIds.length);

        for (uint256 i = 0; i < typeIds.length; i++) {
            // Check if it's a fungible token type
            if (_fungibleTokens[typeIds[i]].maxSupply > 0) {
                require(
                    _fungibleTokens[typeIds[i]].totalMinted + amounts[i]
                        <= _fungibleTokens[typeIds[i]].maxSupply,
                    "Exceeds max supply for some IDs"
                );

                // Increase the totalMinted count for fungible tokens
                _fungibleTokens[typeIds[i]].totalMinted += uint64(amounts[i]);

                idsToMint[i] = typeIds[i];
            } else if (_nftTypes[typeIds[i]].maxMintable > 0) {
                _mintNFTs(typeIds[i], to, amounts[i]);

                // set amount to 0 for NFTs so that it doesn't get minted again
                amounts[i] = 0;
            } else {
                revert("Token type does not exist for some IDs");
            }
        }

        _mintBatch(to, idsToMint, amounts, data);
    }

    /// @notice Burns tokens from a specific address.
    /// @param account Address to burn tokens from.
    /// @param id ID of the token type to burn.
    /// @param amount The amount of tokens to burn.
    function burn(address account, uint256 id, uint256 amount) public onlyAdmin {
        _burn(account, id, amount);
    }

    /// @notice Gets the next token ID for a specific token type.
    /// @param typeId The ID of the token type.
    /// @return The next token ID for the token type.
    function getNextTokenIdForType(uint256 typeId) public view returns (uint256) {
        return _nftTypes[typeId].startingTokenId + _nftTypes[typeId].totalMinted + 1;
    }

    /// @notice Returns the URI for a specific token ID.
    /// @param tokenId The ID of the token.
    /// @return The URI of the token.
    /// @dev Overrides the base implementation to support fungible tokens.
    function uri(uint256 tokenId) public view override returns (string memory) {
        // Check if it's a fungible token type
        if (bytes(_fungibleTokenURIs[tokenId]).length > 0) {
            return _fungibleTokenURIs[tokenId];
        }

        // Check if it's a non-fungible token type
        for (uint256 typeId = 0; typeId < _tokenTypeCounter; typeId++) {
            if (
                tokenId >= _nftTypes[typeId].startingTokenId
                    && tokenId < _nftTypes[typeId].startingTokenId + _nftTypes[typeId].maxMintable
            ) {
                // Construct URI
                return string(abi.encodePacked(_nftTypes[typeId].baseURI, "/", tokenId.toString()));
            }
        }

        // If not found in both, revert to the parent implementation
        return super.uri(tokenId);
    }

    /// @notice Adds an admin to the contract.
    /// @param admin The address of the new admin.
    function addAdmin(address admin) external onlyOwner {
        _admins[admin] = true;
    }

    /// @notice Removes an admin from the contract.
    /// @param admin The address of the admin to remove.
    function removeAdmin(address admin) external onlyOwner {
        _admins[admin] = false;
    }

    /// @notice Checks if an address is an admin of the contract.
    /// @param admin The address to check.
    /// @return True if the address is an admin, false otherwise.
    function isAdmin(address admin) external view returns (bool) {
        return _admins[admin];
    }

    /// @notice Checks if the contract supports a specific interface.
    /// @param interfaceId The interface ID to check for.
    /// @return True if the interface is supported, false otherwise.
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return interfaceId == type(IERC1155).interfaceId || super.supportsInterface(interfaceId);
    }
}

src/TronicMembership.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IERC6551Registry.sol";
import "lib/tokenbound/lib/openzeppelin-contracts/contracts/token/ERC721/ERC721.sol";
import "lib/tokenbound/lib/openzeppelin-contracts/contracts/proxy/utils/Initializable.sol";

/// @title TronicMembership
/// @notice This contract represents the membership token for the Tronic ecosystem.
contract TronicMembership is ERC721, Initializable {
    /// @dev Struct representing a membership tier.
    /// @param tierId The ID of the tier.
    /// @param duration The duration of the tier in seconds.
    /// @param isOpen Whether the tier is open or closed.
    struct MembershipTier {
        string tierId;
        uint128 duration;
        bool isOpen;
    }

    /// @dev Struct representing the membership details of a token.
    /// @param tierIndex The index of the membership tier.
    /// @param timestamp The timestamp of the membership.
    struct TokenMembership {
        uint8 tierIndex;
        uint128 timestamp;
    }

    string private _name;
    string private _symbol;
    string private _baseURI_;

    address public owner;
    address public accountImplementation;
    uint8 private _numTiers;
    uint8 private _maxTiers;
    bool public isElastic;
    bool public isBound;
    uint256 public maxSupply;
    uint256 private _totalBurned;
    uint256 private _totalMinted;

    IERC6551Registry public registry;

    mapping(uint8 => MembershipTier) private _membershipTiers;
    mapping(uint256 => TokenMembership) private _tokenMemberships;
    mapping(address => bool) private _admins;

    /// @dev Modifier to ensure only the owner can call certain functions.
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function");
        _;
    }

    /// @dev Modifier to ensure only the admin can call certain functions.
    modifier onlyAdmin() {
        require(_admins[msg.sender], "Only admin");
        _;
    }

    /// @dev Modifier to ensure a tier exists.
    /// @param tierIndex The index of the tier to check.
    modifier tierExists(uint8 tierIndex) {
        require(tierIndex <= _numTiers, "Tier does not exist");
        _;
    }

    /// @dev Modifier to ensure a token exists.
    /// @param tokenId The ID of the token to check.
    modifier tokenExists(uint256 tokenId) {
        require(_ownerOf(tokenId) != address(0), "Token does not exist");
        _;
    }

    /// @notice Constructor initializes the ERC721 with empty name and symbol.
    /// @dev The name and symbol can be set using the initialize function.
    /// @dev The constructor is left empty because of the proxy pattern used.
    constructor() ERC721("", "") {}

    /// @notice Initializes the contract with given parameters.
    /// @param _accountImplementation Implementation of the account.
    /// @param _registry Address of the registry contract.
    /// @param name_ Name of the token.
    /// @param symbol_ Symbol of the token.
    /// @param uri Base URI of the token.
    /// @param _maxMembershipTiers Maximum number of membership tiers.
    /// @param _maxSupply Maximum supply of the token.
    /// @param _isElastic Whether the max token supply is elastic or not.
    /// @param _isBound Whether the token is soulbound or not.
    /// @param tronicAdmin Address of the initial admin.
    /// @dev This function is called by the tronicMain contract.
    function initialize(
        address payable _accountImplementation,
        address _registry,
        string memory name_,
        string memory symbol_,
        string memory uri,
        uint8 _maxMembershipTiers,
        uint256 _maxSupply,
        bool _isElastic,
        bool _isBound,
        address tronicAdmin
    ) external initializer {
        accountImplementation = _accountImplementation;
        registry = IERC6551Registry(_registry);
        owner = tronicAdmin;
        _admins[tronicAdmin] = true;
        _admins[msg.sender] = true;
        _name = name_;
        _symbol = symbol_;
        _baseURI_ = uri;
        _maxTiers = _maxMembershipTiers;
        maxSupply = _maxSupply;
        isElastic = _isElastic;
        isBound = _isBound;
    }

    /// @notice Mints a new token.
    /// @param to Address to mint the token to.
    /// @return tbaAccount The payable address of the created tokenbound account.
    /// @dev The tokenbound account is created using the registry contract.
    function mint(address to) public onlyAdmin returns (address payable tbaAccount) {
        require(++_totalMinted <= maxSupply, "Max supply reached");
        // Deploy token account
        tbaAccount = payable(
            registry.createAccount(
                accountImplementation,
                block.chainid,
                address(this),
                _totalMinted,
                0, // salt
                abi.encodeWithSignature("initialize()") // init data
            )
        );

        // Mint token
        _mint(to, _totalMinted);
    }

    /// @notice Creates a new membership tier.
    /// @param tierId The ID of the new tier.
    /// @param duration The duration of the new tier in seconds.
    /// @param isOpen Whether the tier is open or closed.
    /// @dev Only callable by admin.
    function createMembershipTier(string memory tierId, uint128 duration, bool isOpen)
        external
        onlyAdmin
    {
        require(++_numTiers <= _maxTiers, "Max Tier limit reached");
        _membershipTiers[_numTiers] =
            MembershipTier({tierId: tierId, duration: duration, isOpen: isOpen});
    }

    /// @notice Creates multiple new membership tiers.
    /// @param tierIds The IDs of the new tiers.
    /// @param durations The durations of the new tiers in seconds.
    /// @param isOpens Whether the tiers are open or closed.
    /// @dev Only callable by admin. Arrays must all have the same length.
    function createMembershipTiers(
        string[] memory tierIds,
        uint128[] memory durations,
        bool[] memory isOpens
    ) external onlyAdmin {
        require(
            isOpens.length == tierIds.length && tierIds.length == durations.length,
            "Input array mismatch"
        );
        require(_numTiers + tierIds.length <= _maxTiers, "Max Tier limit reached");

        for (uint256 i = 0; i < tierIds.length; i++) {
            _membershipTiers[++_numTiers] =
                MembershipTier({tierId: tierIds[i], duration: durations[i], isOpen: isOpens[i]});
        }
    }

    /// @notice Sets the open status of a membership tier.
    /// @param tierIndex The index of the tier to update.
    /// @param isOpen The new open status.
    /// @dev Only callable by admin.
    /// @dev the tier must exist.
    function setMembershipTierOpenStatus(uint8 tierIndex, bool isOpen)
        external
        onlyAdmin
        tierExists(tierIndex)
    {
        _membershipTiers[tierIndex].isOpen = isOpen;
    }

    /// @notice Sets the ID of a membership tier.
    /// @param tierIndex The index of the tier to update.
    /// @param tierId The new tier ID.
    /// @dev Only callable by admin.
    /// @dev the tier must exist.
    function setMembershipTierId(uint8 tierIndex, string memory tierId)
        external
        onlyAdmin
        tierExists(tierIndex)
    {
        _membershipTiers[tierIndex].tierId = tierId;
    }

    /// @notice Sets the duration of a membership tier.
    /// @param tierIndex The index of the tier to update.
    /// @param duration The new duration in seconds.
    /// @dev Only callable by admin.
    /// @dev the tier must exist.
    function setMembershipTierDuration(uint8 tierIndex, uint128 duration)
        external
        onlyAdmin
        tierExists(tierIndex)
    {
        _membershipTiers[tierIndex].duration = duration;
    }

    /// @notice Retrieves the details of a membership tier.
    /// @param tierIndex The index of the tier to retrieve.
    /// @return The details of the tier.
    function getMembershipTierDetails(uint8 tierIndex)
        external
        view
        returns (MembershipTier memory)
    {
        return _membershipTiers[tierIndex];
    }

    /// @notice Retrieves the ID of a membership tier.
    /// @param tierIndex The index of the tier to retrieve.
    /// @return The ID of the tier.
    function getMembershipTierId(uint8 tierIndex) external view returns (string memory) {
        return _membershipTiers[tierIndex].tierId;
    }

    /// @notice Sets the membership details of a specific token.
    /// @param tokenId The ID of the token whose membership details are to be set.
    /// @param tierIndex The index of the membership tier to associate with the token.
    /// @dev This function can only be called by an admin.
    /// @dev The tier must exist.
    /// @dev The token must exist.
    function setTokenMembership(uint256 tokenId, uint8 tierIndex)
        external
        onlyAdmin
        tierExists(tierIndex)
        tokenExists(tokenId)
    {
        _tokenMemberships[tokenId] = TokenMembership(tierIndex, uint128(block.timestamp));
    }

    /// @notice Retrieves the membership details of a specific token.
    /// @param tokenId The ID of the token whose membership details are to be retrieved.
    /// @return The membership details of the token, represented by a `TokenMembership` struct.
    /// @dev The token must exist.
    function getTokenMembership(uint256 tokenId)
        external
        view
        tokenExists(tokenId)
        returns (TokenMembership memory)
    {
        return _tokenMemberships[tokenId];
    }

    /// @notice Retrieves the tokenbound account of a given token ID.
    /// @param tokenId The ID of the token.
    /// @return The address of the tokenbound account.
    function getTBAccount(uint256 tokenId) external view returns (address) {
        return registry.account(accountImplementation, block.chainid, address(this), tokenId, 0);
    }

    /// @notice Retrieves tier index of a given tier ID.
    /// @param tierId The ID of the tier.
    /// @return The index of the tier.
    /// @dev Returns 0 if the tier does not exist.
    function getTierIndexByTierId(string memory tierId) external view returns (uint8) {
        for (uint8 i = 1; i <= _numTiers; i++) {
            string memory candidateTierId = _membershipTiers[i].tierId;
            if (keccak256(abi.encodePacked(candidateTierId)) == keccak256(abi.encodePacked(tierId)))
            {
                return i;
            }
        }

        return 0;
    }

    //function to determine if a token has a valid membership
    /// @notice Checks if a token has a valid membership.
    /// @param tokenId The ID of the token.
    /// @return True if the token has a valid membership, false otherwise.
    /// @dev The token must exist.
    function isValid(uint256 tokenId) external view tokenExists(tokenId) returns (bool) {
        TokenMembership memory membership = _tokenMemberships[tokenId];
        MembershipTier memory tier = _membershipTiers[membership.tierIndex];
        return membership.timestamp + tier.duration > block.timestamp;
    }

    /// @notice Burns a token with the given ID.
    /// @param tokenId ID of the token to burn.
    function burn(uint256 tokenId) external onlyAdmin {
        ++_totalBurned;
        _tokenMemberships[tokenId] = TokenMembership(0, 0);
        _burn(tokenId);
    }

    /// @notice Sets the max supply of the token.
    /// @param _maxSupply The new max supply.
    /// @dev Only callable by admin.
    /// @dev Only callable for elastic tokens.
    /// @dev The max supply must be greater than the total minted.
    function setMaxSupply(uint256 _maxSupply) external onlyAdmin {
        require(isElastic, "Max supply can only be set for elastic tokens");
        require(_maxSupply > _totalMinted, "Max supply must be greater than total minted");
        maxSupply = _maxSupply;
    }

    /// @notice Sets the base URI for the token.
    /// @param uri The new base URI.
    function setBaseURI(string memory uri) external onlyOwner {
        _baseURI_ = uri;
    }

    /// @notice Returns the name of the token.
    /// @return The name of the token.
    function name() public view override returns (string memory) {
        return _name;
    }

    /// @notice Returns the symbol of the token.
    /// @return The symbol of the token.
    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    /// @notice Adds an admin.
    /// @param _admin The address of the new admin.
    /// @dev Only callable by owner.
    function addAdmin(address _admin) external onlyOwner {
        _admins[_admin] = true;
    }

    /// @notice Removes an admin.
    /// @param _admin The address of the admin to remove.
    /// @dev Only callable by owner.
    function removeAdmin(address _admin) external onlyOwner {
        _admins[_admin] = false;
    }

    /// @notice Checks if an address is an admin.
    /// @param _admin The address to check.
    /// @return True if the address is an admin, false otherwise.
    function isAdmin(address _admin) external view returns (bool) {
        return _admins[_admin];
    }

    /// @notice Updates the implementation of the account.
    /// @param _accountImplementation The new account implementation address.
    /// @dev Only callable by owner.
    function updateImplementation(address payable _accountImplementation) external onlyOwner {
        accountImplementation = _accountImplementation;
    }

    /// @notice Overrides the supportsInterface function to include support for ERC721.
    /// @param interfaceId The interface ID to check for.
    /// @return True if the interface is supported, false otherwise.
    function supportsInterface(bytes4 interfaceId) public view override(ERC721) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /// @notice Transfers ownership of the contract to a new owner.
    /// @param newOwner The address of the new owner.
    /// @dev Only callable by owner.
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "New owner address cannot be zero");
        owner = newOwner;
    }

    /// @notice Transfers an unbound token from one address to another.
    /// @param from The address to transfer the token from.
    /// @param to The address to transfer the token to.
    /// @param tokenId The ID of the token to transfer.
    /// @dev This function overrides the transferFrom function of ERC721.
    /// @dev it reverts if the token is bound.
    function transferFrom(address from, address to, uint256 tokenId) public override {
        require(!isBound, "Token is bound");
        super.transferFrom(from, to, tokenId);
    }

    /// @notice Safely transfers an unbound token from one address to another.
    /// @param from The address to transfer the token from.
    /// @param to The address to transfer the token to.
    /// @param tokenId The ID of the token to transfer.
    /// @dev This function overrides the safeTransferFrom function of ERC721.
    /// @dev it reverts if the token is bound.
    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory _data)
        public
        override
    {
        require(!isBound, "Token is bound");
        super.safeTransferFrom(from, to, tokenId, _data);
    }

    /// @notice Returns the total supply of the token.
    /// @return The total supply of the token.
    function totalSupply() external view returns (uint256) {
        return _totalMinted - _totalBurned;
    }
}

